package com.EcoMove.Controladores;import com.EcoMove.Entidades.Estacion;import com.EcoMove.Repositorios.EstacionRepositorio;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/estaciones")public class EstacionControlador {    private final EstacionRepositorio estacionRepositorio;    public EstacionControlador(EstacionRepositorio estacionRepositorio) {        this.estacionRepositorio = estacionRepositorio;    }    @PostMapping    public ResponseEntity<Estacion> crear(@RequestBody Estacion e) {        Estacion nueva = estacionRepositorio.save(e);        return ResponseEntity.status(HttpStatus.CREATED).body(nueva);    }    @GetMapping    public ResponseEntity<List<Estacion>> listar() {        return ResponseEntity.ok(estacionRepositorio.findAll());    }    @GetMapping("/{id}")    public ResponseEntity<Estacion> buscar(@PathVariable String id) {        return estacionRepositorio.findById(id)                .map(ResponseEntity::ok)                .orElse(ResponseEntity.notFound().build());    }}