package com.EcoMove.Controladores;import com.EcoMove.Entidades.Pago;import com.EcoMove.Repositorios.PagoRepositorio;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.util.List;@RestController@RequestMapping("/api/pagos")public class PagoController {    private final PagoRepositorio pagoRepositorio;    public PagoController(PagoRepositorio pagoRepositorio) {        this.pagoRepositorio = pagoRepositorio;    }    @GetMapping    public ResponseEntity<List<Pago>> listar() {        List<Pago> pagos = pagoRepositorio.findAll();        if (pagos.isEmpty()) {            return ResponseEntity.noContent().build();        }        return ResponseEntity.ok(pagos);    }    @GetMapping("/{id}")    public ResponseEntity<Pago> buscar(@PathVariable String id) {        return pagoRepositorio.findById(id)                .map(ResponseEntity::ok)                .orElse(ResponseEntity.notFound().build());    }}