package com.EcoMove.Controladores;import com.EcoMove.Entidades.Transporte;import com.EcoMove.Repositorios.TransporteRepositorio;import org.springframework.http.HttpStatus;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/transportes")public class TransporteController {    private final TransporteRepositorio transporteRepositorio;    public TransporteController(TransporteRepositorio transporteRepositorio) {        this.transporteRepositorio = transporteRepositorio;    }    @PostMapping    public ResponseEntity<Transporte> crear(@RequestBody Transporte t) {        Transporte nuevo = transporteRepositorio.save(t);        return ResponseEntity.status(HttpStatus.CREATED).body(nuevo);    }    @GetMapping    public ResponseEntity<List<Transporte>> listar() {        return ResponseEntity.ok(transporteRepositorio.findAll());    }    @GetMapping("/{id}")    public ResponseEntity<Transporte> buscar(@PathVariable String id) {        return transporteRepositorio.findById(id)                .map(ResponseEntity::ok)                .orElse(ResponseEntity.notFound().build());    }}