package com.EcoMove.Controladores;import com.EcoMove.Entidades.Usuario;import com.EcoMove.Repositorios.UsuarioRepositorio;import org.springframework.http.ResponseEntity;import org.springframework.web.bind.annotation.*;import java.util.List;@RestController@RequestMapping("/api/usuarios")public class UsuarioControlador {    private final UsuarioRepositorio usuarioRepositorio;    public UsuarioControlador(UsuarioRepositorio usuarioRepositorio) {        this.usuarioRepositorio = usuarioRepositorio;    }    @PostMapping    public ResponseEntity<Usuario> crearUsuario(@RequestBody Usuario u) {        Usuario usuarioGuardado = usuarioRepositorio.save(u);        return ResponseEntity.ok(usuarioGuardado);    }    @GetMapping    public ResponseEntity<List<Usuario>> listar() {        return ResponseEntity.ok(usuarioRepositorio.findAll());    }    @GetMapping("/{id}")    public ResponseEntity<Usuario> buscar(@PathVariable String id) {        return usuarioRepositorio.findById(id)                .map(ResponseEntity::ok)                .orElse(ResponseEntity.notFound().build());    }}