package com.EcoMove.Entidades;import lombok.Data;import org.springframework.data.annotation.Id;import org.springframework.data.mongodb.core.mapping.Document;import java.time.LocalDateTime;@Data@Document(collection = "prestamos")public class Prestamo {    @Id    private String id;    private String usuarioId;    private String transporteId;    private String estacionOrigenId;    private String estacionDestinoId;    private LocalDateTime inicio;    private LocalDateTime fin;    private double costo;    private String pagoId;}